COMMANDS.COMMAND:
        if (makeIn.equals("projectroot")) {
            location = projectroot;
        }
        else if (makeIn.equals("customloc")) {
            location = customlocation;
        }
        else location=sourcedir;
		return "cd " + location;

			buf = new StringBuffer();

			if(format.equals("dvi"))
				buf.append("latex");
			else
				buf.append("pdflatex");

			if(!font_max.equals(""))
			{
				buf.append(" -font-max \"");
				buf.append(font_max);
				buf.append('"');
			}

			if(!font_mem_size.equals(""))
			{
				buf.append(" -font-mem-size \"");
				buf.append(font_mem_size);
				buf.append('"');
			}



			if(!interaction.equals(""))
			{
				buf.append(" -interaction ");
				buf.append(interaction);
			}


            if (haltonerror)
                buf.append(" -halt-on-error");

			if(src_specials)
				buf.append(" -src-specials");

			if(c_style_errors)
				buf.append(" -file-line-error");

			if(shell_escape)
				buf.append(" -shell-escape");

			if(silent)
				buf.append(" -silent");

			if(tcx)
				buf.append(" -tcx");

			buf.append(" \"");
			source = source.replace('\\','/');
			buf.append(source);
			buf.append('"');

			// return value
			buf.toString();
