COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("javac");

buffer.setProperty("java.classpath", classpath);

if(!classpath.equals(""))
	buf.append(" -classpath \""+classpath+"\"");

/*
if(!sourcepath.equals(""))
	buf.append(" -sourcepath \""+sourcepath+"\"");
*/

/*
if(!extdirs.equals(""))
	buf.append(" -extdirs \""+extdirs+"\"");
*/

if(!outputpath.equals(""))
	buf.append(" -d \""+outputpath+"\"");

if(debug)
	buf.append(" -g");

if(optimize)
	buf.append(" -O");

if(!warn)
	buf.append(" -nowarn");

if(deprecation)
	buf.append(" -deprecation");

if(!encoding.equals(""))
	buf.append(" -encoding "+encoding);

if (!args.equals(""))
	buf.append(" "+args);

buf.append(" \""+source+"\"");

// return value
return buf.toString();
