COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("ant");

if (search) {
	buf.append(" -s \""+buildfile+"\"");
}
else {
	buf.append(" -f \""+buildfile+"\"");
}

if (!classpath.equals(""))
	buf.append(" -lib \""+classpath+"\"");

if (emacs)
	buf.append(" -e");

if (!args.equals(""))
	buf.append(" "+args);

if (!rules.equals(""))
	buf.append(" "+rules);

// return value
return buf.toString();
