COMMANDS.COMMAND:
StringBuffer buf = new StringBuffer("diff ");

if(recursive)
	buf.append(" -r");

if(!ignore.equals(""))
{
	buf.append(" -X ");
	buf.append(ignore);
}

if(binary)
	buf.append(" -a");

if(whitespace)
	buf.append(" -b");

if(blankLines)
	buf.append(" -B");

if(softTabs)
	buf.append(" -t");

if(ignoreCase)
	buf.append(" -i");

if(newFile)
	buf.append(" -N");

if(identical)
	buf.append(" -s");

if(algorithm.equals("slow"))
	buf.append(" -d");
else if(algorithm.equals("large"))
	buf.append(" -H");

if(output.equals("brief"))
	buf.append(" -q");
else if(output.equals("context"))
{
	buf.append(" -C ");
	buf.append(context);
}
else if(output.equals("ed"))
	buf.append(" -e");
else if(output.equals("rcs"))
	buf.append(" -n");
else if(output.equals("sideBySide"))
	buf.append(" -y");
else if(output.equals("unified"))
{
	buf.append(" -U ");
	buf.append(context);
}

buf.append(" \"");
buf.append(from);
buf.append("\" \"");
buf.append(to);
buf.append('"');
