COMMANDS.COMMAND:
buf = new StringBuffer("openjade");

if(!stylesheet.equals(""))
{
	buf.append(" -d \"");
	buf.append(stylesheet);
	buf.append('"');
}

if(!dir.equals(""))
{
	buf.append(" -D \"");
	buf.append(dir);
	buf.append('"');
}

buf.append(" -t sgml");

if(nochunks)
	buf.append(" -Vnochunks");

if(rootchunk)
	buf.append(" -Vrootchunk");

if(!extra.equals(""))
{
	buf.append(' ');
	buf.append(extra);
}

if(!dcl.equals(""))
{
	buf.append(" \"");
	buf.append(dcl);
	buf.append('"');
}

buf.append(" \"");
buf.append(file);
buf.append('"');

// return value
buf.toString();
