COMMANDS.COMMAND:
buf = new StringBuffer(command);

if(!makefile.equals(""))
{
	buf.append(" -f \"");
	buf.append(makefile);
	buf.append('"');
}

if(!targets.equals(""))
{
	buf.append(' ');
	buf.append(targets);
}

if(debug)
	buf.append(" -d");

if(norun)
	buf.append(" -n");

if(output.equals("awt"))
	buf.append(" -w");

if(output.equals("swing"))
	buf.append(" -s");

// return value
buf.toString();
