COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("javac");

jEdit.setProperty("CLASSPATH", classpath_env);
setenv("CLASSPATH", classpath_env);
classpath = getBufferClassPath(buffer);

/* if(!bootclasspath.equals(""))
{
	buf.append(" -bootclasspath \"");
	buf.append(bootclasspath);
	buf.append('"');
} */

if(!classpath.equals(""))
{
	buf.append(" -classpath \"");
	buf.append(classpath);
	buf.append('"');
}

if(!sourcepath.equals(""))
{
	buf.append(" -sourcepath \"");
	buf.append(sourcepath);
	buf.append('"');
}

if(!extdirs.equals(""))
{
	buf.append(" -extdirs \"");
	buf.append(extdirs);
	buf.append('"');
}

if(!outputpath.equals(""))
{
	buf.append(" -d \"");
	buf.append(outputpath);
	buf.append('"');
}

if(debug)
	buf.append(" -g");

if(optimize)
	buf.append(" -O");

if(!warn)
	buf.append(" -nowarn");

if(deprecation)
	buf.append(" -deprecation");

/* if(verbose)
	buf.append(" -verbose"); */

if(!encoding.equals(""))
{
	buf.append(" -encoding ");
	buf.append(encoding);
}

buf.append(" \"");
buf.append(source);
buf.append('"');

// return value
buf.toString();
