COMMANDS.COMMAND:
buf = new StringBuffer("jikes +E");

jEdit.setProperty("CLASSPATH", classpath_env);
setenv("CLASSPATH", classpath_env);
classpath = getBufferClassPath(buffer);

if(!classpath.equals(""))
{
	buf.append(" -classpath \"");
	buf.append(classpath);
	buf.append('"');
}

if(!outputpath.equals(""))
{
	buf.append(" -d \"");
	buf.append(outputpath);
	buf.append('"');
}

if(debug)
	buf.append(" -g");

if(optimize)
	buf.append(" -O");

if(!warn)
	buf.append(" -nowarn");

if(deprecation)
	buf.append(" -deprecation");

if(pedantic)
	buf.append(" +P");

buf.append(" \"");
buf.append(source);
buf.append('"');

// return value
buf.toString();
