COMMANDS.COMMAND:
buf = new StringBuffer(command);

if(!makefile.equals(""))
{
	buf.append(" -buildfile \"");
	buf.append(makefile);
	buf.append('"');
}

if(!targets.equals(""))
{
	buf.append(' ');
	buf.append(targets);
}

if(!output.equals("normal"))
{
	buf.append(" -");
	buf.append(output);
}

if(emacs)
	buf.append(" -emacs");

if(!extra.equals(""))
{
	buf.append(' ');
	buf.append(extra);
}

// return value
buf.toString();
