COMMANDS.COMMAND:
buf = new StringBuffer("openjade");

if(!stylesheet.equals(""))
{
	buf.append(" -d \"");
	buf.append(stylesheet);
	buf.append('"');
}

buf.append(" -t tex");

if(!extra_jade.equals(""))
{
	buf.append(' ');
	buf.append(extra_jade);
}

if(!dcl.equals(""))
{
	buf.append(" \"");
	buf.append(dcl);
	buf.append('"');
}

buf.append(" \"");
buf.append(file);
buf.append('"');

// return value
buf.toString();

tex()
{
	buf = new StringBuffer(tex);

	index = file.lastIndexOf('.');
	if(file != -1)
		file = file.substring(0,index) + ".tex";

	buf.append(" \"");
	buf.append(file);
	buf.append('"');

	return buf;
}

tex();

buf = new StringBuffer(preview);

index = file.lastIndexOf('.');
if(file != -1)
	file = file.substring(0,index) + ".pdf";

buf.append(" \"");
buf.append(file);
buf.append('"');

// return value
buf.toString();
