COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("java");

jEdit.setProperty("CLASSPATH", classpath_env);
setenv("CLASSPATH", classpath_env);
classpath = getBufferClassPath(buffer);

/* if(vm.equals("client"))
	; // default
else if(vm.equals("server"))
	buf.append(" -server");
else if(vm.equals("classic"))
	buf.append(" -classic"); */

if(!classpath.equals(""))
{
	buf.append(" -classpath \"");
	buf.append(classpath);
	buf.append('"');
}

/* if(verbose_class)
	buf.append(" -verbose:class");

if(verbose_gc)
	buf.append(" -verbose:gc");

if(verbose_jni)
	buf.append(" -verbose:jni");

if(vm_X.equals("interp"))
	buf.append(" -Xint");

if(!bootclasspath.equals(""))
{
	buf.append(" -Xbootclasspath");
	if(bootclasspath_mode.equals("append"))
		buf.append("/a");
	else if(bootclasspath_mode.equals("prepend"))
		buf.append("/p");
	buf.append(':');
