}

if(classgc)
	buf.append(" -Xclassgc");

if(incgc)
	buf.append(" -Xincgc");

if(!nobatch)
	buf.append(" -Xbatch");

if(prof)
	buf.append(" -Xprof");

if(future)
	buf.append(" -Xfuture");

if(rs)
	buf.append(" -Xrs"); */

if(!ms.equals(""))
{
	buf.append(" -ms");
	buf.append(ms);
	buf.append('m');
}

if(!mx.equals(""))
{
	buf.append(" -mx");
	buf.append(mx);
	buf.append('m');
}

if(!ss.equals(""))
{
	buf.append(" -ss");
	buf.append(ss);
	buf.append('k');
}

if(!extra.equals(""))
{
	buf.append(' ');
	buf.append(extra);
}

if(jar.endsWith(".jar"))
{
	buf.append(" -jar \"");
	buf.append(jar);
	buf.append('"');
}
else
{
	buf.append(' ');
	buf.append(jar);
}

if(!args.equals(""))
{
	buf.append(' ');
	buf.append(args);
}

// return value
buf.toString();
