COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("make");

d = buffer.getDirectory();

if (search) {
	while (!new File(d, makefile).exists()) {
		newD = MiscUtilities.getParentOfPath(d);
		if (d == newD) {
			// we've reached the top, no makefile found
			makefile = null;
			d = buffer.getDirectory();
			break;
		}

		d = newD;
	}

	if (makefile != null)
		buf.append(" -f \""+makefile+"\"");
}

if (!search || makefile == null)
	ruleName = MiscUtilities.getFileNameNoExtension(buffer.getName());

buf.append(" -C \""+d+"\"");

if (dry)
	buf.append(" -n");

if (keep)
	buf.append(" -k");

if (!params.equals(""))
	buf.append(" "+params);

if (!ruleName.equals(""))
buf.append(" "+ruleName);

// return value
return buf.toString();
