COMMANDS.COMMAND:
buf = new StringBuffer();
buf.append("java -cp \"$GROOVY");

if(!classpath.equals(""))
{
	buf.append(File.pathSeparator);
	buf.append(classpath);
}
buf.append('"');

if(!ms.equals(""))
{
	buf.append(" -ms");
	buf.append(ms);
	buf.append('m');
}

if(!mx.equals(""))
{
	buf.append(" -mx");
	buf.append(mx);
	buf.append('m');
}

if(!ss.equals(""))
{
	buf.append(" -ss");
	buf.append(ss);
	buf.append('k');
}

if(!extra.equals(""))
{
	buf.append(' ');
	buf.append(extra);
}

buf.append(" groovy.lang.GroovyShell ");
buf.append("\""+script+"\"");

if(!args.equals(""))
{
	buf.append(' ');
	buf.append(args);
}

// return value
buf.toString();
