ateRadioButtons(propertyPrefix);		bufferRadio.setAlignmentX(Component.LEFT_ALIGNMENT);		add(bufferRadio);		fileRadio.setAlignmentX(Component.LEFT_ALIGNMENT);		add(fileRadio);		//add(radioPanel);	}		@Override	public void setEnabled(boolean enabled){		super.setEnabled(enabled);		radioSelectAction.setEnabled(enabled);		// disable everything if enabled is false, reactivate only		// if isFileSelected otherwise		setFileSelectionEnabled(enabled && isFileSelected());	}		/**	 * call this to add a component for file operations, like	 * a text-field and a browse button.	 * @param	sourceField	component to be added	 */	protected final void setSourceField(JComponent sourceField){		sourceField.setAlignmentX(Component.LEFT_ALIGNMENT);		add(sourceField);		add(Box.createVerticalGlue());	}		/**	 * called on actions on the radio buttons.	 * @param	enabled	is the 'file' radio-button selected ?	 */	protected abstract void setFileSelectionEnabled(boolean enabled);		private void createRadioButtons(String propertyPrefix) {  	  ButtonGroup radioGroup = new ButtonGroup();  	  bufferRadio = radioSelectAction.getRadioButton(propertyPrefix+".buffer");  	  fileRadio = radioSelectAction.getRadioButton(propertyPrefix+".file");  	    	  bufferRadio.setActionCommand("buffer");  	  bufferRadio.setSelected(jEdit.getBooleanProperty(propertyPrefix+LAST_CURRENT_BUFFER));  	  fileRadio.setActionCommand("file");  	  fileRadio.setSelected(!jEdit.getBooleanProperty(propertyPrefix+LAST_CURRENT_BUFFER));  	    	  radioGroup.add(bufferRadio);  	  radioGroup.add(fileRadio);		}		public boolean isFileSelected() {		return fileRadio.isSelected();	}		public void setFileSelected(boolean fileSelected) {		fileRadio.setSelected(fileSelected);		bufferRadio.setSelected(!fileSelected);		setFileSelectionEnabled(fileSelected);	}	private class RadioSelectAction extends XsltAction {		private String propertyPrefix;				RadioSelectAction(String propertyPrefix){			super(propertyPrefix+".radioselect");			this.propertyPrefix=propertyPrefix;		}				public void actionPerformed(ActionEvent e) {			if ("buffer".equals(e.getActionCommand())) {				setFileSelectionEnabled(false);				jEdit.setBooleanProperty(propertyPrefix+LAST_CURRENT_BUFFER,true);			}else if ("file".equals(e.getActionCommand())) { 				setFileSelectionEnabled(true);				jEdit.setBooleanProperty(propertyPrefix+LAST_CURRENT_BUFFER,false);			}		}	}}